
package rsaencryption.utils;

import java.math.BigInteger;
import static org.junit.Assert.assertEquals;
import org.junit.Test;

public class UtilsTest {
    
    @Test
    public void hexToDecimalWorks() {
        String a = "A";
        String a1 = "a1";
        String two = "2";
        String some = "ABCDEF0123456789";
        String lots = "";
        assertEquals(new BigInteger("10"), Utils.hexToDecimal(a));
        assertEquals(new BigInteger("161"), Utils.hexToDecimal(a1));
        assertEquals(new BigInteger("2"), Utils.hexToDecimal(two));
        assertEquals(new BigInteger("12379813738877118345"), Utils.hexToDecimal(some));
        assertEquals(new BigInteger(""), Utils.hexToDecimal(lots));
    }
    
    @Test
    public void decimalToHexWorks() {
        BigInteger a = new BigInteger("10");
        BigInteger a1 = new BigInteger("161");
        BigInteger two = new BigInteger("2");
        BigInteger some = new BigInteger("12379813738877118345");
        BigInteger lots = new BigInteger("");
        assertEquals("A", Utils.decimalToHex(a));
        assertEquals("A1", Utils.decimalToHex(a1));
        assertEquals("2", Utils.decimalToHex(two));
        assertEquals("ABCDEF0123456789", Utils.decimalToHex(some));
        assertEquals("", Utils.decimalToHex(lots));
    }
    
    @Test
    public void stringToDecimalWorks() {
        assertEquals(new BigInteger("97"), Utils.stringToDecimal("a"));
        assertEquals(new BigInteger("24929"), Utils.stringToDecimal("aa"));
        assertEquals(new BigInteger("7873748523504041982393638547319"), Utils.stringToDecimal("cat says meow"));
    }
    
    @Test
    public void decimalToStringWorks() {
        assertEquals("a", Utils.decimalToString(new BigInteger("97")));
        assertEquals("aa", Utils.decimalToString(new BigInteger("24929")));
        assertEquals("cat says meow", Utils.decimalToString(new BigInteger("7873748523504041982393638547319")));
    }
}
