
package rsaencryption.utils;

import datastructures.MyBigInteger;
import static org.junit.Assert.assertEquals;
import org.junit.Test;

public class UtilsTest {
    
    @Test
    public void hexToDecimalWorks() {
        String a = "A";
        String a1 = "a1";
        String two = "2";
        String some = "ABCDEF0123456789";
        String lots = "";
        assertEquals(new MyBigInteger("10"), Utils.hexToDecimal(a));
        assertEquals(new MyBigInteger("161"), Utils.hexToDecimal(a1));
        assertEquals(new MyBigInteger("2"), Utils.hexToDecimal(two));
        assertEquals(new MyBigInteger("12379813738877118345"), Utils.hexToDecimal(some));
        assertEquals(new MyBigInteger(""), Utils.hexToDecimal(lots));
    }
    
    @Test
    public void decimalToHexWorks() {
        MyBigInteger a = new MyBigInteger("10");
        MyBigInteger a1 = new MyBigInteger("161");
        MyBigInteger two = new MyBigInteger("2");
        MyBigInteger some = new MyBigInteger("12379813738877118345");
        MyBigInteger lots = new MyBigInteger("");
        assertEquals("A", Utils.decimalToHex(a));
        assertEquals("A1", Utils.decimalToHex(a1));
        assertEquals("2", Utils.decimalToHex(two));
        assertEquals("ABCDEF0123456789", Utils.decimalToHex(some));
        assertEquals("", Utils.decimalToHex(lots));
    }
    
    @Test
    public void stringToDecimalWorks() {
        assertEquals(new MyBigInteger("97"), Utils.stringToDecimal("a"));
        assertEquals(new MyBigInteger("24929"), Utils.stringToDecimal("aa"));
        assertEquals(new MyBigInteger("7873748523504041982393638547319"), Utils.stringToDecimal("cat says meow"));
    }
    
    @Test
    public void decimalToStringWorks() {
        assertEquals("a", Utils.decimalToString(new MyBigInteger("97")));
        assertEquals("aa", Utils.decimalToString(new MyBigInteger("24929")));
        assertEquals("cat says meow", Utils.decimalToString(new MyBigInteger("7873748523504041982393638547319")));
    }
}
